mapper.select:
{call UPA_WMS_AREA_TOPDOWN_LIST(
#{domainId, mode=IN, jdbcType=VARCHAR, javaType=string},
#{areaSeq, mode=IN, jdbcType=INTEGER, javaType=java.lang.Long}
) }

{call UPA_WMS_AREA_COMP_CHECK(
	#{areaSeq, mode=IN, jdbcType=INTEGER, javaType=java.lang.Long},
	#{success, mode=OUT, jdbcType=INTEGER, javaType=java.lang.Long}
) }

{#{returnValue, mode=OUT, jdbcType=INTEGER, javaType=int} = call UPA_WMS_AREA_COMP_DELETE(
	#{areaSeq, mode=IN, jdbcType=INTEGER, javaType=java.lang.Long}
) }
